INSERT INTO `libwild_posts` VALUES("113","1","2017-02-25 23:52:08","2017-02-26 06:52:08","[av_slideshow_full size=\'featured\' min_height=\'0px\' stretch=\'\' animation=\'slide\' autoplay=\'false\' interval=\'5\' control_layout=\'av-control-default\' src=\'\' attachment=\'\' attachment_size=\'\' position=\'top left\' repeat=\'no-repeat\' attach=\'scroll\']
