INSERT INTO `libwild_posts` VALUES("422","1","2017-03-07 19:31:23","2017-03-08 02:31:23","[av_slideshow_full size=\'featured\' min_height=\'0px\' stretch=\'\' animation=\'slide\' autoplay=\'false\' interval=\'5\' control_layout=\'av-control-default\' src=\'\' attachment=\'\' attachment_size=\'\' position=\'top left\' repeat=\'no-repeat\' attach=\'scroll\']
