INSERT INTO `libwild_posts` VALUES("433","1","2017-03-08 17:25:16","2017-03-09 00:25:16","[av_slideshow_full size=\'featured\' min_height=\'0px\' stretch=\'\' animation=\'slide\' autoplay=\'false\' interval=\'5\' control_layout=\'av-control-default\' src=\'\' attachment=\'\' attachment_size=\'\' position=\'top left\' repeat=\'no-repeat\' attach=\'scroll\']
